import React, { useEffect, useRef, useState } from "react";

export default function VoiceAnalyzer({ stream }) {
  const [volume, setVolume] = useState(0);
  const [speaking, setSpeaking] = useState(false);
  const [confidenceText, setConfidenceText] = useState("Listening...");
  const [confidenceScore, setConfidenceScore] = useState(0);

  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const rafRef = useRef(null);

  // Rolling values for stability
  const avgVolumeRef = useRef(0);
  const speakingFramesRef = useRef(0);
  const totalFramesRef = useRef(0);

  useEffect(() => {
    if (!stream) return;

    const AudioContext =
      window.AudioContext || window.webkitAudioContext;
    const audioContext = new AudioContext();

    if (audioContext.state === "suspended") {
      audioContext.resume();
    }

    const source = audioContext.createMediaStreamSource(stream);
    const analyser = audioContext.createAnalyser();
    analyser.fftSize = 512;

    source.connect(analyser);

    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    audioContextRef.current = audioContext;
    analyserRef.current = analyser;

    const analyze = () => {
      analyser.getByteTimeDomainData(dataArray);

      let sum = 0;
      for (let i = 0; i < bufferLength; i++) {
        const v = (dataArray[i] - 128) / 128;
        sum += v * v;
      }

      const rms = Math.sqrt(sum / bufferLength);
      setVolume(rms);

      // Speaking detection
      const isSpeaking = rms > 0.02;
      setSpeaking(isSpeaking);

      totalFramesRef.current += 1;
      if (isSpeaking) speakingFramesRef.current += 1;

      // Smooth volume (EMA)
      avgVolumeRef.current =
        avgVolumeRef.current * 0.9 + rms * 0.1;

      // ðŸ§  Confidence text
      if (avgVolumeRef.current > 0.07) {
        setConfidenceText("Strong & confident voice âœ…");
      } else if (avgVolumeRef.current > 0.03) {
        setConfidenceText("Clear but speak slightly louder ðŸ—£ï¸");
      } else {
        setConfidenceText("Too quiet / hesitant âš ï¸");
      }

      // ðŸŽ¯ Confidence Score Calculation
      const volumeScore = Math.min(
        Math.max((avgVolumeRef.current / 0.08) * 50, 0),
        50
      );

      const speakingRatio =
        speakingFramesRef.current / totalFramesRef.current;

      const speakingScore = Math.min(speakingRatio * 50, 50);

      const finalScore = Math.round(volumeScore + speakingScore);

      setConfidenceScore(finalScore);

      rafRef.current = requestAnimationFrame(analyze);
    };

    analyze();

    return () => {
      if (rafRef.current) cancelAnimationFrame(rafRef.current);
      audioContext.close();
    };
  }, [stream]);

  return (
    <div
      style={{
        marginTop: "16px",
        padding: "14px",
        borderRadius: "12px",
        background: "#f8f9ff",
        boxShadow: "0 8px 24px rgba(0,0,0,0.08)",
      }}
    >
      <h4>ðŸŽ¤ Live Voice Feedback</h4>

      <p>
        Status:{" "}
        <strong style={{ color: speaking ? "green" : "gray" }}>
          {speaking ? "Speaking" : "Silent"}
        </strong>
      </p>

      {/* Volume Bar */}
      <div
        style={{
          height: "10px",
          width: "100%",
          background: "#ddd",
          borderRadius: "6px",
          overflow: "hidden",
          marginBottom: "10px",
        }}
      >
        <div
          style={{
            height: "100%",
            width: `${Math.min(volume * 400, 100)}%`,
            background: "#3a7fff",
            transition: "width 0.1s",
          }}
        />
      </div>

      <p>ðŸ§  Voice Insight:</p>
      <strong>{confidenceText}</strong>

      <hr style={{ margin: "12px 0" }} />

      <p>
        ðŸŽ¯ <strong>Confidence Score:</strong>{" "}
        <span
          style={{
            color:
              confidenceScore > 70
                ? "green"
                : confidenceScore > 40
                ? "#eab308"
                : "red",
          }}
        >
          {confidenceScore} / 100
        </span>
      </p>
    </div>
  );
}
