import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  LineChart,
  Line,
  CartesianGrid,
  Legend,
} from "recharts";

export default function PerformanceDashboard({ score }) {
  const barData = [
    { name: "Confidence", value: score },
    { name: "Pace", value: Math.min(score - 10, 100) },
    { name: "Engagement", value: Math.min(score - 5, 100) },
  ];

  const pieData = [
    { name: "Voice", value: 40 },
    { name: "Pace", value: 30 },
    { name: "Posture", value: 30 },
  ];

  const timelineData = [
    { time: "0s", value: 40 },
    { time: "15s", value: 55 },
    { time: "30s", value: 65 },
    { time: "45s", value: score },
  ];

  const COLORS = ["#4f46e5", "#22c55e", "#f59e0b"];

  return (
    <div
      style={{
        marginTop: 30,
        padding: 20,
        background: "#fff",
        borderRadius: 16,
      }}
    >
      <h3>ðŸ“Š Interview Performance Report</h3>

      {/* Bar Chart */}
      <div style={{ height: 250 }}>
        <ResponsiveContainer width="100%" height="100%">
          <BarChart data={barData}>
            <XAxis dataKey="name" />
            <YAxis
              domain={[0, 100]}
              label={{
                value: "Score (0-100)",
                angle: -90,
                position: "insideLeft",
              }}
            />
            <Tooltip />
            <Bar
              dataKey="value"
              fill="#4f46e5"
              radius={[6, 6, 0, 0]}
              barSize={35}
            />
          </BarChart>
        </ResponsiveContainer>
      </div>

      {/* Donut Chart */}
      <div style={{ height: 250, marginTop: 30 }}>
        <ResponsiveContainer width="100%" height="100%">
          <PieChart>
            <Pie
              data={pieData}
              innerRadius={60}
              outerRadius={100}
              paddingAngle={5}
              dataKey="value"
            >
              {pieData.map((entry, index) => (
                <Cell key={index} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip />
          </PieChart>
        </ResponsiveContainer>
      </div>

      {/* Line Chart */}
      <div style={{ height: 300, marginTop: 30 }}>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart
            data={timelineData}
            margin={{ top: 20, right: 30, left: 20, bottom: 20 }}
          >
            <CartesianGrid strokeDasharray="3 3" />

            <XAxis
              dataKey="time"
              label={{
                value: "Time (seconds)",
                position: "insideBottom",
                offset: -10,
              }}
            />

            <YAxis
              domain={[0, 100]}
              label={{
                value: "Confidence Score (0â€“100)",
                angle: -90,
                position: "insideLeft",
              }}
            />

            <Tooltip />
            <Legend />

            <Line
              type="monotone"
              dataKey="value"
              name="Confidence Score"
              stroke="#4f46e5"
              strokeWidth={3}
              dot={{ r: 4 }}
              activeDot={{ r: 6 }}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}
