import React, { useEffect, useRef, useState } from "react";
import VoiceAnalyzer from "./VoiceAnalyzer";

export default function VideoRecorder() {
  const videoRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);

  const [stream, setStream] = useState(null);
  const [recording, setRecording] = useState(false);
  const [videoURL, setVideoURL] = useState(null);

  // üé• Start camera on load
  useEffect(() => {
    async function initCamera() {
      try {
        const mediaStream = await navigator.mediaDevices.getUserMedia({
          video: true,
          audio: true,
        });

        setStream(mediaStream);
        if (videoRef.current) {
          videoRef.current.srcObject = mediaStream;
        }
      } catch (err) {
        alert("Camera or microphone permission denied");
      }
    }

    initCamera();

    return () => {
      if (stream) {
        stream.getTracks().forEach((track) => track.stop());
      }
    };
  }, []);

  // ‚ñ∂Ô∏è Start recording (USER ACTION ‚Äî IMPORTANT)
  const startRecording = async () => {
    if (!stream) return;

    // üîë FORCE audio context unlock
    const AudioContext =
      window.AudioContext || window.webkitAudioContext;
    const tempContext = new AudioContext();
    if (tempContext.state === "suspended") {
      await tempContext.resume();
    }
    tempContext.close();

    chunksRef.current = [];

    let options = {};
    if (MediaRecorder.isTypeSupported("video/webm;codecs=vp8,opus")) {
      options.mimeType = "video/webm;codecs=vp8,opus";
    } else if (MediaRecorder.isTypeSupported("video/webm")) {
      options.mimeType = "video/webm";
    }

    const recorder = new MediaRecorder(stream, options);
    mediaRecorderRef.current = recorder;

    recorder.ondataavailable = (e) => {
      if (e.data.size > 0) {
        chunksRef.current.push(e.data);
      }
    };

    recorder.onstop = () => {
      const blob = new Blob(chunksRef.current, {
        type: recorder.mimeType || "video/webm",
      });
      const url = URL.createObjectURL(blob);
      setVideoURL(url);
    };

    recorder.start();
    setRecording(true);
  };

  // ‚èπ Stop recording
  const stopRecording = () => {
    mediaRecorderRef.current.stop();
    setRecording(false);
  };

  return (
    <div className="video-recorder">
      <h3>üé• Interview Camera</h3>

      {/* Live Preview */}
      <video
        ref={videoRef}
        autoPlay
        muted
        playsInline
        style={{ width: "100%", borderRadius: "12px" }}
      />
      {recording && <PostureAnalyzer videoRef={videoRef} />}

      {/* üé§ Live Voice Feedback (NOW WORKS) */}
      {recording && stream && <VoiceAnalyzer stream={stream} />}

      {/* Controls */}
      <div style={{ marginTop: "12px" }}>
        {!recording ? (
          <button className="btn primary" onClick={startRecording}>
            üî¥ Start Recording
          </button>
        ) : (
          <button className="btn ghost" onClick={stopRecording}>
            ‚èπ Stop Recording
          </button>
        )}
      </div>

      {/* Playback */}
      {videoURL && (
        <div style={{ marginTop: "16px" }}>
          <h4>‚ñ∂Ô∏è Playback</h4>
          <video
            src={videoURL}
            controls
            style={{ width: "100%", borderRadius: "12px" }}
          />
        </div>
      )}
    </div>
  );
}
