import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/home.css";

export default function Home() {
  const navigate = useNavigate();
  const [direction, setDirection] = useState(null);
  const [transitioning, setTransitioning] = useState(false);

  const triggerTransition = (route, dir) => {
    setDirection(dir);
    setTransitioning(true);

    setTimeout(() => {
      navigate(route, { state: { direction: dir } });
    }, 800); // must match CSS duration
  };

  return (
    <div
      className={`home-root 
        ${transitioning && direction === "right" ? "roll-right" : ""}
        ${transitioning && direction === "left" ? "roll-left" : ""}
      `}
    >
      {transitioning && (
        <div className="electric-wrapper">
          <div className="electric-sweep"></div>
        </div>
      )}

      <div className="home-inner container">
        <header className="topbar">
          <div className="brand">
            <img src="/logo192.png" alt="Marg AI" className="brand-logo" />
            <div className="brand-text">
              <div className="brand-name">Marg AI</div>
              <div className="brand-sub">AI Interview Coach</div>
            </div>
          </div>
        </header>

        <section className="hero">
          <div className="hero-left">
            <h1 className="hero-title">
              Practice interviews.<br />
              Get feedback.<br />
              Land the job.
            </h1>

            <p className="hero-sub">
              Instant AI feedback on code and communication —
              video, audio and code analysis combined into one clean report.
            </p>

            <div className="hero-ctas">
              <button
                className="btn primary"
                onClick={() => triggerTransition("/mock-interview", "left")}
              >
                Start Mock Interview
              </button>

              <button
                className="btn secondary"
                onClick={() => triggerTransition("/practice", "right")}
              >
                Practice Coding
              </button>
            </div>
          </div>

          <div className="hero-right">
            <div className="tv-frame">
              <video
                className="demo-video"
                autoPlay
                loop
                muted
                playsInline
              >
                <source src="/demo-video.mp4" type="video/mp4" />
              </video>
              <div className="scanlines"></div>
            </div>
          </div>
        </section>

        <footer className="home-footer">
          © {new Date().getFullYear()} Marg AI
        </footer>
      </div>
    </div>
  );
}
