import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import PracticeConsole from "../components/PracticeConsole";
import QuestionBank from "../components/QuestionBank";
import "../styles/home.css";

export default function Practice() {
  const [language, setLanguage] = useState("javascript");
  const [selectedCode, setSelectedCode] = useState("");
  const location = useLocation();
  const [enterClass, setEnterClass] = useState("");

  // ðŸ”¥ Detect direction from Home
  useEffect(() => {
    if (location.state?.direction === "right") {
      setEnterClass("enter-from-right");
    } else if (location.state?.direction === "left") {
      setEnterClass("enter-from-left");
    }
  }, [location.state]);

  return (
    <div
      className={`practice-page ${enterClass}`}
      style={{
        minHeight: "100vh",
        padding: "50px",
        background: "linear-gradient(135deg,#f8fafc,#eef2ff)",
        fontFamily: "Inter, sans-serif",
      }}
    >
      {/* HEADER */}
      <div
        style={{
          marginBottom: 40,
          background: "#ffffff",
          padding: 35,
          borderRadius: 25,
          boxShadow: "0 20px 60px rgba(99,102,241,0.08)",
        }}
      >
        <h1 style={{ marginBottom: 10 }}>ðŸ’» Practice Coding</h1>
        <p style={{ color: "#64748b", marginBottom: 20 }}>
          Write, run, and improve your code with intelligent AI feedback.
        </p>

        <select
          value={language}
          onChange={(e) => setLanguage(e.target.value)}
          style={{
            padding: "10px 18px",
            borderRadius: 14,
            border: "1px solid #e2e8f0",
            background: "#fff",
            fontWeight: 500,
            cursor: "pointer",
            boxShadow: "0 5px 20px rgba(0,0,0,0.04)",
          }}
        >
          <option value="javascript">JavaScript</option>
          <option value="python">Python</option>
          <option value="java">Java</option>
        </select>
      </div>

      {/* MAIN LAYOUT */}
      <div
        style={{
          display: "flex",
          gap: 40,
        }}
      >
        {/* LEFT â€” QUESTION BANK */}
        <div
          style={{
            flex: 0.8,
            background: "#ffffff",
            padding: 30,
            borderRadius: 25,
            boxShadow: "0 20px 60px rgba(59,130,246,0.08)",
          }}
        >
          <h3 style={{ marginBottom: 20 }}>ðŸ“š Question Bank</h3>
          <QuestionBank onSelect={setSelectedCode} />
        </div>

        {/* RIGHT â€” CODE CONSOLE */}
        <div
          style={{
            flex: 1.4,
            background: "#ffffff",
            padding: 30,
            borderRadius: 25,
            boxShadow: "0 20px 60px rgba(59,130,246,0.08)",
          }}
        >
          <h3 style={{ marginBottom: 20 }}>ðŸ§  Code Workspace</h3>
          <PracticeConsole
            language={language}
            externalCode={selectedCode}
          />
        </div>
      </div>
    </div>
  );
}
