// client/src/pages/MockInterview.jsx
import React, { useEffect, useRef, useState } from "react";
import VideoRecorder from "../components/VideoRecorder";

/**
 * MockInterview page:
 * - has a small set of adaptive questions
 * - uses browser TTS (speechSynthesis) to speak questions like an AI interviewer
 * - shows webcam preview and lets user record answers per question
 * - after upload: shows returned analysis JSON (transcript, scores)
 */

const QUESTIONS = [
  { id: "q1", text: "Tell me about yourself and your background." , timeLimit: 40 },
  { id: "q2", text: "Explain how you would find the middle node of a linked list." , timeLimit: 60 },
  { id: "q3", text: "How do you optimize a slow database query?" , timeLimit: 45 }
];

export default function MockInterview() {
  const [index, setIndex] = useState(0);
  const [inProgress, setInProgress] = useState(false);
  const [analysis, setAnalysis] = useState(null);
  const ttsRef = useRef(window.speechSynthesis);

  useEffect(() => {
    return () => {
      // cleanup TTS on unmount
      if (ttsRef.current && ttsRef.current.speaking) ttsRef.current.cancel();
    };
  }, []);

  function speak(text) {
    if (!("speechSynthesis" in window)) return alert("Text-to-speech not supported");
    ttsRef.current.cancel();
    const u = new SpeechSynthesisUtterance(text);
    u.lang = "en-US";
    u.rate = 1.0;
    u.pitch = 1.0;
    ttsRef.current.speak(u);
  }

  function askQuestion(i) {
    const q = QUESTIONS[i];
    setAnalysis(null); // clear previous
    setInProgress(true);
    speak(q.text);
  }

  function handleUploadComplete(data) {
    // data from server: { success: true, analysis: {...} } or error
    setAnalysis(data.analysis || { error: data.error || "No analysis returned" });
  }

  function nextQuestion() {
    if (index + 1 < QUESTIONS.length) setIndex(index + 1);
    else alert("Interview finished. You can review your analyses below.");
  }

  return (
    <div style={{padding:28}}>
      <h2>Mock Interview</h2>
      <div style={{display:"flex", gap:20, alignItems:"flex-start", marginTop:12}}>
        <div style={{width:360}}>
          <div style={{background:"#0b1014", padding:16, borderRadius:8}}>
            <h3 style={{marginTop:0}}>Interviewer</h3>
            <p style={{color:"#9aa6b2"}}>Question {index+1} of {QUESTIONS.length}</p>
            <div style={{fontWeight:700, marginTop:12}}>{QUESTIONS[index].text}</div>
            <div style={{marginTop:12, display:"flex", gap:8}}>
              <button className="btn primary" onClick={() => askQuestion(index)}>Ask Question (TTS)</button>
              <button className="btn" onClick={() => setAnalysis(null)}>Reset Analysis</button>
              <button className="btn" onClick={nextQuestion}>Next Question</button>
            </div>
          </div>

          <div style={{marginTop:20}}>
            <h4>Analysis</h4>
            {analysis ? (
              <div className="card" style={{padding:12}}>
                <div><strong>Transcript:</strong></div>
                <div style={{marginTop:6, color:"#cfe7ff"}}>{analysis.transcript}</div>
                <div style={{marginTop:12}}>
                  <div>Clarity: {analysis.clarity_score}/10</div>
                  <div>Confidence: {analysis.confidence_score}/10</div>
                  <div>Body Language: {analysis.body_language_score}/10</div>
                  <div>Filler words: {analysis.filler_count}</div>
                </div>
                <div style={{marginTop:10}}>
                  <strong>Suggestions:</strong>
                  <ul>{(analysis.suggestions || []).map((s,i)=>(<li key={i}>{s}</li>))}</ul>
                </div>
              </div>
            ) : (
              <div style={{color:"#9aa6b2"}}>No analysis yet. Record your answer and upload to get instant feedback.</div>
            )}
          </div>
        </div>

        <div style={{flex:1}}>
          <VideoRecorder onUploadComplete={handleUploadComplete} />
        </div>
      </div>
    </div>
  );
}
