import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  LineChart,
  Line,
  CartesianGrid,
} from "recharts";

export default function PerformanceDashboard({ score }) {
  const barData = [
    { name: "Confidence", value: score },
    { name: "Pace", value: Math.min(score - 10, 100) },
    { name: "Engagement", value: Math.min(score - 5, 100) },
  ];

  const pieData = [
    { name: "Voice", value: 40 },
    { name: "Pace", value: 30 },
    { name: "Posture", value: 30 },
  ];

  const timelineData = [
    { time: "0s", value: 40 },
    { time: "15s", value: 55 },
    { time: "30s", value: 65 },
    { time: "45s", value: score },
  ];

  const COLORS = ["#4f46e5", "#22c55e", "#f59e0b"];

  return (
    <div style={{ marginTop: 30, padding: 20, background: "#fff", borderRadius: 16 }}>
      <h3>ðŸ“Š Interview Performance Report</h3>

      {/* Bar Chart */}
      <div style={{ height: 250 }}>
        <ResponsiveContainer width="100%" height="100%">
          <BarChart data={barData}>
            <XAxis dataKey="name" />
            <YAxis domain={[0, 100]} />
            <Tooltip />
            <Bar dataKey="value" fill="#4f46e5" radius={[8, 8, 0, 0]} />
          </BarChart>
        </ResponsiveContainer>
      </div>

      {/* Donut Chart */}
      <div style={{ height: 250, marginTop: 30 }}>
        <ResponsiveContainer width="100%" height="100%">
          <PieChart>
            <Pie
              data={pieData}
              innerRadius={60}
              outerRadius={100}
              paddingAngle={5}
              dataKey="value"
            >
              {pieData.map((entry, index) => (
                <Cell key={index} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
          </PieChart>
        </ResponsiveContainer>
      </div>

      {/* Line Chart */}
      <div style={{ height: 250, marginTop: 30 }}>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={timelineData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="time" />
            <YAxis domain={[0, 100]} />
            <Tooltip />
            <Line type="monotone" dataKey="value" stroke="#4f46e5" strokeWidth={3} />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}
