import React, { useState } from "react";
import { useParams } from "react-router-dom";
import PracticeConsole from "../components/PracticeConsole";

const BACKTRACKING_QUESTIONS = [
  {
    title: "Letter Combinations of a Phone Number",
    description: `Given a string containing digits from 2-9 inclusive, return all possible letter combinations.`,
  },
  {
    title: "Generate Parentheses",
    description: `Given n pairs of parentheses, generate all combinations of well-formed parentheses.`,
  },
  {
    title: "Combination Sum",
    description: `Return all unique combinations that sum to target.`,
  },
  {
    title: "Permutations",
    description: `Return all possible permutations.`,
  },
  {
    title: "N-Queens",
    description: `Place n queens such that no two attack each other.`,
    image: "/nqueens.png",
  },
  {
    title: "Subsets",
    description: `Return all possible subsets.`,
  },
  {
    title: "Palindrome Partitioning",
    description: `Return all palindrome partitions.`,
  },
];

export default function TopicPractice() {
  const { topic } = useParams();
  const questions = BACKTRACKING_QUESTIONS;

  const [index, setIndex] = useState(0);
  const [status, setStatus] = useState(
    Array(questions.length).fill("unattempted")
  );

  function handleSubmit(isCorrect) {
    const updated = [...status];
    updated[index] = isCorrect ? "correct" : "wrong";
    setStatus(updated);
  }

  function nextQuestion() {
    if (status[index] === "unattempted") {
      const updated = [...status];
      updated[index] = "skipped";
      setStatus(updated);
    }

    if (index + 1 < questions.length) {
      setIndex(index + 1);
    }
  }

  return (
    <div style={{ padding: "40px", background: "#f8fafc", minHeight: "100vh" }}>

      {/* QUESTION PROGRESS BAR */}
      <div style={{ display: "flex", gap: 10, marginBottom: 30 }}>
        {questions.map((_, i) => (
          <div
            key={i}
            style={{
              width: 40,
              height: 40,
              borderRadius: 10,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontWeight: 600,
              background:
                status[i] === "correct"
                  ? "#22c55e"
                  : status[i] === "wrong"
                  ? "#ef4444"
                  : status[i] === "skipped"
                  ? "#8b5cf6"
                  : "#e2e8f0",
              color: status[i] === "unattempted" ? "#1e293b" : "#fff",
            }}
          >
            {i + 1}
          </div>
        ))}
      </div>

      <div style={{ display: "flex", gap: 40 }}>

        {/* LEFT SIDE — DESCRIPTION */}
        <div style={{ flex: 1, background: "#ffffff", padding: 30, borderRadius: 20 }}>
          <h2>{questions[index].title}</h2>
          <p style={{ marginTop: 20, color: "#475569" }}>
            {questions[index].description}
          </p>

          {questions[index].image && (
            <img
              src={questions[index].image}
              alt="visual"
              style={{ marginTop: 20, width: "100%", borderRadius: 10 }}
            />
          )}
        </div>

        {/* RIGHT SIDE — CODE AREA */}
        <div style={{ flex: 1.2, background: "#ffffff", padding: 30, borderRadius: 20 }}>

          <PracticeConsole />

          <div style={{ marginTop: 20, display: "flex", gap: 20 }}>
            <button
              onClick={() => handleSubmit(true)}
              style={{
                padding: "10px 20px",
                background: "#22c55e",
                color: "#fff",
                borderRadius: 10,
                border: "none",
                cursor: "pointer",
              }}
            >
              Submit (Correct)
            </button>

            <button
              onClick={() => handleSubmit(false)}
              style={{
                padding: "10px 20px",
                background: "#ef4444",
                color: "#fff",
                borderRadius: 10,
                border: "none",
                cursor: "pointer",
              }}
            >
              Submit (Wrong)
            </button>

            <button
              onClick={nextQuestion}
              style={{
                padding: "10px 20px",
                background: "#6366f1",
                color: "#fff",
                borderRadius: 10,
                border: "none",
                cursor: "pointer",
              }}
            >
              Next
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
