// client/src/components/PostureAnalyzer.jsx

import React, { useEffect, useRef } from "react";

export default function PostureAnalyzer({ videoRef, onScoreUpdate }) {
  const faceFrames = useRef(0);
  const totalFrames = useRef(0);
  const headMovement = useRef(0);
  const lastNose = useRef(null);
  const intervalRef = useRef(null);

  useEffect(() => {
    if (!videoRef?.current) return;

    const script = document.createElement("script");
    script.src =
      "https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh/face_mesh.js";
    script.async = true;

    script.onload = () => {
      const faceMesh = new window.FaceMesh({
        locateFile: (file) =>
          `https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh/${file}`,
      });

      faceMesh.setOptions({
        maxNumFaces: 1,
        refineLandmarks: true,
        minDetectionConfidence: 0.5,
        minTrackingConfidence: 0.5,
      });

      faceMesh.onResults((results) => {
        totalFrames.current++;

        if (results.multiFaceLandmarks) {
          faceFrames.current++;

          const landmarks = results.multiFaceLandmarks[0];

          // ðŸ”¹ Eye contact logic
          const leftEye = landmarks[33];
          const rightEye = landmarks[263];
          const nose = landmarks[1];

          const eyeCenterX = (leftEye.x + rightEye.x) / 2;
          const gazeOffset = Math.abs(nose.x - eyeCenterX);
          const eyeScore = 1 - Math.min(gazeOffset * 3, 1);

          // ðŸ”¹ Head movement stability
          if (lastNose.current) {
            const dx = nose.x - lastNose.current.x;
            const dy = nose.y - lastNose.current.y;
            const movement = Math.sqrt(dx * dx + dy * dy);
            headMovement.current += movement;
          }

          lastNose.current = nose;

          // ðŸ”¹ Engagement ratio
          const engagement =
            faceFrames.current / totalFrames.current;

          // ðŸ”¹ Stability score
          const stability = Math.max(
            0,
            1 - headMovement.current * 5
          );

          // ðŸ”¹ Final posture score
          const postureScore =
            eyeScore * 0.4 +
            engagement * 0.3 +
            stability * 0.3;

          if (onScoreUpdate) {
            onScoreUpdate(postureScore);
          }
        }
      });

      intervalRef.current = setInterval(async () => {
        if (videoRef.current.readyState >= 2) {
          await faceMesh.send({ image: videoRef.current });
        }
      }, 100);
    };

    document.body.appendChild(script);

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [videoRef, onScoreUpdate]);

  return null;
}
