// client/src/pages/MockInterview.jsx

import React, { useEffect, useRef, useState } from "react";
import VideoRecorder from "../components/VideoRecorder";

const QUESTIONS = [
  { id: "q1", text: "Tell me about yourself.", timeLimit: 40 },
  { id: "q2", text: "Tell me about a difficult problem you solved.", timeLimit: 60 },
  { id: "q3", text: "What are your strengths and weaknesses?", timeLimit: 50 },
  { id: "q4", text: "Describe a time you failed and what you learned.", timeLimit: 50 },
  { id: "q5", text: "How do you handle feedback?", timeLimit: 40 },
];

export default function MockInterview() {
  const [index, setIndex] = useState(0);
  const [analysis, setAnalysis] = useState(null);
  const [timeLeft, setTimeLeft] = useState(QUESTIONS[0].timeLimit);
  const timerRef = useRef(null);

  useEffect(() => {
    return () => clearInterval(timerRef.current);
  }, []);

  function startTimer() {
    clearInterval(timerRef.current);
    setTimeLeft(QUESTIONS[index].timeLimit);

    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  }

  function askQuestion() {
    setAnalysis(null);
    startTimer();
  }

  function nextQuestion() {
    clearInterval(timerRef.current);
    if (index + 1 < QUESTIONS.length) {
      setIndex(index + 1);
      setTimeLeft(QUESTIONS[index + 1].timeLimit);
      setAnalysis(null);
    }
  }

  function handleUploadComplete(data) {
    setAnalysis(data.analysis);
  }

  return (
    <div
      style={{
        display: "flex",
        height: "100vh",
        background: "#f8fafc",
        padding: "40px",
        gap: "40px",
        fontFamily: "Inter, sans-serif",
      }}
    >
      {/* LEFT SIDE — QUESTIONS */}
      <div style={{ flex: 1.2 }}>
        <h2 style={{ marginBottom: 10 }}>Mock Interview</h2>

        <div
          style={{
            background: "#ffffff",
            padding: 30,
            borderRadius: 20,
            boxShadow: "0 10px 30px rgba(0,0,0,0.05)",
          }}
        >
          <p style={{ color: "#64748b" }}>
            Question {index + 1} of {QUESTIONS.length}
          </p>

          <h3 style={{ fontSize: 22, marginTop: 10 }}>
            {QUESTIONS[index].text}
          </h3>

          <p
            style={{
              marginTop: 10,
              fontWeight: 600,
              color: timeLeft <= 5 ? "#ef4444" : "#10b981",
            }}
          >
            ⏳ {timeLeft}s
          </p>

          <div style={{ marginTop: 20, display: "flex", gap: 15 }}>
            <button
              onClick={askQuestion}
              style={{
                padding: "10px 20px",
                borderRadius: 10,
                border: "none",
                background: "#3b82f6",
                color: "#fff",
                cursor: "pointer",
              }}
            >
              Ask
            </button>

            <button
              onClick={nextQuestion}
              style={{
                padding: "10px 20px",
                borderRadius: 10,
                border: "1px solid #e2e8f0",
                background: "#fff",
                cursor: "pointer",
              }}
            >
              Next
            </button>
          </div>
        </div>

        {/* AI Feedback */}
        <div
          style={{
            marginTop: 30,
            background: "#ffffff",
            padding: 25,
            borderRadius: 20,
            boxShadow: "0 10px 30px rgba(0,0,0,0.05)",
            minHeight: 220,
          }}
        >
          <h4 style={{ marginBottom: 10 }}>Live AI Feedback</h4>

          {analysis ? (
            <>
              <p><strong>Confidence:</strong> {analysis.confidence_score}/10</p>
              <p><strong>Pace:</strong> {analysis.pace_score}/10</p>
              <p><strong>Engagement:</strong> {analysis.engagement_score}/10</p>
              <p><strong>WPM:</strong> {analysis.words_per_minute}</p>
            </>
          ) : (
            <p style={{ color: "#94a3b8" }}>
              Record your answer to see real-time evaluation.
            </p>
          )}
        </div>
      </div>

      {/* RIGHT SIDE — VIDEO */}
      <div style={{ flex: 0.8 }}>
        <div
          style={{
            background: "#ffffff",
            padding: 20,
            borderRadius: 20,
            boxShadow: "0 10px 30px rgba(0,0,0,0.05)",
          }}
        >
          <VideoRecorder onUploadComplete={handleUploadComplete} />
        </div>
      </div>
    </div>
  );
}
