// client/src/pages/MockInterview.jsx

import React, { useEffect, useRef, useState } from "react";
import VideoRecorder from "../components/VideoRecorder";

const QUESTIONS = [
  { id: "q1", text: "Tell me about yourself.", timeLimit: 40 },
  { id: "q2", text: "Tell me about a difficult problem you solved.", timeLimit: 60 },
  { id: "q3", text: "What are your strengths and weaknesses?", timeLimit: 50 },
  { id: "q4", text: "Describe a time you failed ‚Äî and what you learned.", timeLimit: 50 },
  { id: "q5", text: "How do you handle feedback?", timeLimit: 40 },
  { id: "q6", text: "Out of all the candidates, why should we hire you?", timeLimit: 45 },
  { id: "q7", text: "What is your leadership style?", timeLimit: 40 },
  { id: "q8", text: "What do you like to do outside of work?", timeLimit: 30 },
];

export default function MockInterview() {
  const [index, setIndex] = useState(0);
  const [analysis, setAnalysis] = useState(null);
  const [timeLeft, setTimeLeft] = useState(QUESTIONS[0].timeLimit);
  const timerRef = useRef(null);
  const ttsRef = useRef(window.speechSynthesis);

  useEffect(() => {
    return () => {
      if (ttsRef.current?.speaking) ttsRef.current.cancel();
      clearInterval(timerRef.current);
    };
  }, []);

  function speak(text) {
    if (!("speechSynthesis" in window))
      return alert("Text-to-speech not supported");

    ttsRef.current.cancel();
    const u = new SpeechSynthesisUtterance(text);
    u.lang = "en-US";
    u.rate = 1;
    ttsRef.current.speak(u);
  }

  function startTimer() {
    clearInterval(timerRef.current);
    setTimeLeft(QUESTIONS[index].timeLimit);

    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  }

  function askQuestion(i) {
    setAnalysis(null);
    speak(QUESTIONS[i].text);
    startTimer();
  }

  function handleUploadComplete(data) {
    setAnalysis(
      data.analysis || { error: data.error || "No analysis returned" }
    );
  }

  function nextQuestion() {
    clearInterval(timerRef.current);

    if (index + 1 < QUESTIONS.length) {
      setIndex(index + 1);
      setAnalysis(null);
      setTimeLeft(QUESTIONS[index + 1].timeLimit);
    } else {
      alert("Interview Finished üéâ Review your performance.");
    }
  }

  return (
    <div
      style={{
        padding: 32,
        background: "#0f172a",
        minHeight: "100vh",
        color: "#fff",
      }}
    >
      <h2 style={{ marginBottom: 20 }}>ü§ñ AI Mock Interview</h2>

      <div style={{ display: "flex", gap: 30 }}>
        {/* LEFT PANEL */}
        <div style={{ width: 400 }}>
          <div
            style={{
              background: "#1e293b",
              padding: 20,
              borderRadius: 16,
              boxShadow: "0 10px 30px rgba(0,0,0,0.4)",
            }}
          >
            <p style={{ color: "#94a3b8" }}>
              Question {index + 1} of {QUESTIONS.length}
            </p>

            <div
              style={{
                fontWeight: 700,
                fontSize: 18,
                marginTop: 12,
                background: "linear-gradient(90deg, #4f46e5, #06b6d4)",
                WebkitBackgroundClip: "text",
                WebkitTextFillColor: "transparent",
              }}
            >
              {QUESTIONS[index].text}
            </div>

            <div
              style={{
                marginTop: 15,
                fontSize: 14,
                color: timeLeft <= 5 ? "#ef4444" : "#22c55e",
              }}
            >
              ‚è≥ Time Left: {timeLeft}s
            </div>

            <div style={{ marginTop: 15, display: "flex", gap: 10 }}>
              <button className="btn primary" onClick={() => askQuestion(index)}>
                Ask Question
              </button>

              <button className="btn" onClick={nextQuestion}>
                Next
              </button>
            </div>
          </div>

          {/* ANALYSIS */}
          <div style={{ marginTop: 25 }}>
            <h4 style={{ marginBottom: 10 }}>üìä AI Feedback</h4>

            {analysis ? (
              <div
                style={{
                  background: "#1e293b",
                  padding: 16,
                  borderRadius: 12,
                }}
              >
                <div>
                  <strong>Transcript:</strong>
                  <div style={{ marginTop: 6, color: "#cbd5e1" }}>
                    {analysis.transcript}
                  </div>
                </div>

                <div style={{ marginTop: 12 }}>
                  <div>Clarity: {analysis.clarity_score}/10</div>
                  <div>Confidence: {analysis.confidence_score}/10</div>
                  <div>Body Language: {analysis.body_language_score}/10</div>
                  <div>Filler Words: {analysis.filler_count}</div>
                </div>

                <div style={{ marginTop: 12 }}>
                  <strong>Suggestions:</strong>
                  <ul>
                    {(analysis.suggestions || []).map((s, i) => (
                      <li key={i}>{s}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ) : (
              <div style={{ color: "#94a3b8" }}>
                Record your answer to receive AI feedback.
              </div>
            )}
          </div>
        </div>

        {/* RIGHT PANEL ‚Äî FIXED TEXT VISIBILITY */}
        <div
          style={{
            flex: 1,
            background: "#ffffff",
            color: "#000000", // üëà THIS FIXES INVISIBLE TEXT
            padding: 20,
            borderRadius: 16,
          }}
        >
          <VideoRecorder onUploadComplete={handleUploadComplete} />
        </div>
      </div>
    </div>
  );
}
