import React from "react";
import { useLocation } from "react-router-dom";
import "../styles/report.css";

export default function Report(){
  const { state } = useLocation();
  const { analysis, transcript } = state || {};

  if(!analysis) return <h2 style={{padding:40}}>No report found</h2>;

  return (
    <div className="report-page">

      {/* HEADER */}
      <div className="report-header">
        <h2>ü§ñ AI Interview Report</h2>
        <p>Date: {new Date().toDateString()}</p>
      </div>

      <div className="report-body">

        {/* LEFT */}
        <div className="report-left">

          <div className="score-card">
            <Score title="Confidence" value={analysis.confidence_score}/>
            <Score title="Pace" value={analysis.pace_score}/>
            <Score title="Engagement" value={analysis.engagement_score}/>
          </div>

          <div className="transcript-box">
            <h3>Transcript</h3>
            <p>{transcript}</p>
          </div>

          <button className="download-btn">Download Report</button>

        </div>

        {/* RIGHT */}
        <div className="report-right">
          <h3>AI Feedback</h3>
          {analysis.ai_feedback?.map((f,i)=>(
            <div key={i} className="feedback-card">üí° {f}</div>
          ))}
        </div>

      </div>
    </div>
  );
}

// ‚≠ê Score Component
function Score({ title, value }){
  return (
    <div className="score">
      <h4>{title}</h4>
      <div className="circle">{value}/10</div>
    </div>
  );
}
