// client/src/components/PostureAnalyzer.jsx

import { useEffect } from "react";
import { FaceMesh } from "@mediapipe/face_mesh";

export default function PostureAnalyzer({ videoRef, onScoreUpdate }) {
  useEffect(() => {
    if (!videoRef?.current) return;

    const faceMesh = new FaceMesh({
      locateFile: (file) =>
        `https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh/${file}`,
    });

    faceMesh.setOptions({
      maxNumFaces: 1,
      refineLandmarks: true,
      minDetectionConfidence: 0.6,
      minTrackingConfidence: 0.6,
    });

    let lastEyeRatio = 0;
    let blinkCount = 0;

    faceMesh.onResults((results) => {
      if (!results.multiFaceLandmarks) return;

      const landmarks = results.multiFaceLandmarks[0];

      // ----------------------------
      // 1️⃣ Eye Contact Score
      // ----------------------------
      const leftEye = landmarks[33];
      const rightEye = landmarks[263];
      const noseTip = landmarks[1];

      const eyeCenterX = (leftEye.x + rightEye.x) / 2;
      const gazeOffset = Math.abs(noseTip.x - eyeCenterX);

      const eyeContactScore = 1 - Math.min(gazeOffset * 3, 1);

      // ----------------------------
      // 2️⃣ Head Tilt Score
      // ----------------------------
      const tilt = Math.abs(leftEye.y - rightEye.y);
      const headTiltScore = 1 - Math.min(tilt * 5, 1);

      // ----------------------------
      // 3️⃣ Blink Stability
      // ----------------------------
      const eyeOpenRatio =
        Math.abs(landmarks[159].y - landmarks[145].y) +
        Math.abs(landmarks[386].y - landmarks[374].y);

      if (lastEyeRatio !== 0 && eyeOpenRatio < lastEyeRatio * 0.6) {
        blinkCount++;
      }

      lastEyeRatio = eyeOpenRatio;

      const blinkScore = blinkCount < 15 ? 1 : 0.6;

      // ----------------------------
      // 4️⃣ Jaw Movement (Nervous)
      // ----------------------------
      const jaw = landmarks[152];
      const chinMovement = Math.abs(jaw.x - noseTip.x);

      const movementScore = 1 - Math.min(chinMovement * 2, 1);

      // ----------------------------
      // FINAL CONFIDENCE SCORE
      // ----------------------------
      const finalScore =
        eyeContactScore * 0.4 +
        headTiltScore * 0.2 +
        blinkScore * 0.2 +
        movementScore * 0.2;

      onScoreUpdate(finalScore);
    });

    let animationFrame;

    const analyze = async () => {
      if (videoRef.current) {
        await faceMesh.send({ image: videoRef.current });
      }
      animationFrame = requestAnimationFrame(analyze);
    };

    analyze();

    return () => {
      cancelAnimationFrame(animationFrame);
    };
  }, [videoRef, onScoreUpdate]);

  return null; // No UI — only analysis
}
