import React from "react";
import { PRACTICE_QUESTIONS } from "../data/practiceQuestions";

export default function QuestionBank({ topic = "Backtracking", onSelect }) {
  const questions = PRACTICE_QUESTIONS[topic] || [];

  return (
    <div>
      {questions.map((q, index) => (
        <div
          key={index}
          style={{
            marginBottom: "30px",
            padding: "25px",
            borderRadius: "20px",
            background: "#f8fafc",
            cursor: "pointer",
            transition: "0.3s ease"
          }}
          onClick={() => onSelect(q.description)}
        >
          {/* BOLD TITLE */}
          <h3 style={{ fontWeight: "bold", fontSize: "18px" }}>
            {index + 1}. {q.title}
          </h3>

          <pre style={{ whiteSpace: "pre-wrap", marginTop: "10px" }}>
            {q.description}
          </pre>

          {q.image && (
            <img
              src={q.image}
              alt="N Queens"
              style={{ width: "100%", marginTop: "15px", borderRadius: "12px" }}
            />
          )}
        </div>
      ))}
    </div>
  );
}
