import React, { useEffect, useRef, useState } from "react";

export default function VoiceAnalyzer({ stream, recording }) {
  const [volume, setVolume] = useState(0);
  const [speaking, setSpeaking] = useState(false);

  const [confidenceText, setConfidenceText] = useState("Listening...");
  const [confidenceScore, setConfidenceScore] = useState(0);
  const [confidenceLabel, setConfidenceLabel] = useState("‚Äî");

  const [paceFeedback, setPaceFeedback] = useState("Analyzing pace...");
  const [summary, setSummary] = useState(null);
  const [postureFeedback] = useState("Maintain eye contact & upright posture");

  const analyserRef = useRef(null);
  const rafRef = useRef(null);

  const avgVolumeRef = useRef(0);
  const speakingFramesRef = useRef(0);
  const totalFramesRef = useRef(0);

  const lastSuggestionTimeRef = useRef(0);
  const lastPaceCheckRef = useRef(Date.now());

  const scoreSumRef = useRef(0);
  const scoreCountRef = useRef(0);
  const lastScoreUpdateRef = useRef(Date.now());

  // ‚úÖ RESET METRICS WHEN RECORDING STARTS
  useEffect(() => {
    if (recording) {
      avgVolumeRef.current = 0;
      speakingFramesRef.current = 0;
      totalFramesRef.current = 0;
      scoreSumRef.current = 0;
      scoreCountRef.current = 0;
      setSummary(null);
      setConfidenceScore(0);
      setConfidenceLabel("‚Äî");
    }
  }, [recording]);

  useEffect(() => {
    if (!stream) return;

    const AudioContext =
      window.AudioContext || window.webkitAudioContext;
    const audioContext = new AudioContext();
    audioContext.resume();

    const source = audioContext.createMediaStreamSource(stream);
    const analyser = audioContext.createAnalyser();
    analyser.fftSize = 512;
    source.connect(analyser);

    analyserRef.current = analyser;
    const dataArray = new Uint8Array(analyser.frequencyBinCount);

    const analyze = () => {
      analyser.getByteTimeDomainData(dataArray);

      let sum = 0;
      for (let i = 0; i < dataArray.length; i++) {
        const v = (dataArray[i] - 128) / 128;
        sum += v * v;
      }

      const rms = Math.sqrt(sum / dataArray.length);
      setVolume(rms);

      const isSpeaking = rms > 0.02;
      setSpeaking(isSpeaking);

      totalFramesRef.current++;
      if (isSpeaking) speakingFramesRef.current++;

      avgVolumeRef.current =
        avgVolumeRef.current * 0.9 + rms * 0.1;

      const now = Date.now();

      // üîπ Stabilized voice suggestion
      if (now - lastSuggestionTimeRef.current > 1500) {
        if (avgVolumeRef.current > 0.07) {
          setConfidenceText("Strong & confident voice ‚úÖ");
        } else if (avgVolumeRef.current > 0.03) {
          setConfidenceText("Clear but speak slightly louder üó£Ô∏è");
        } else {
          setConfidenceText("Too quiet / hesitant ‚ö†Ô∏è");
        }
        lastSuggestionTimeRef.current = now;
      }

      // üîπ Instant confidence score
      const volumeScore = Math.min(
        Math.max((avgVolumeRef.current / 0.08) * 50, 0),
        50
      );

      const speakingRatio =
        totalFramesRef.current > 0
          ? speakingFramesRef.current / totalFramesRef.current
          : 0;

      const speakingScore = Math.min(speakingRatio * 50, 50);
      const instantScore = volumeScore + speakingScore;

      scoreSumRef.current += instantScore;
      scoreCountRef.current++;

      // üîπ Average confidence every 45s
      if (now - lastScoreUpdateRef.current >= 45000) {
        const avgScore =
          scoreCountRef.current > 0
            ? scoreSumRef.current / scoreCountRef.current
            : 0;

        const rounded = Math.round(avgScore);
        setConfidenceScore(rounded);

        if (rounded >= 75) setConfidenceLabel("Excellent ‚≠ê");
        else if (rounded >= 50) setConfidenceLabel("Good üëç");
        else setConfidenceLabel("Needs Improvement ‚ö†Ô∏è");

        scoreSumRef.current = 0;
        scoreCountRef.current = 0;
        lastScoreUpdateRef.current = now;
      }

      // üîπ Pace detection (5s window)
      if (now - lastPaceCheckRef.current >= 5000) {
        if (speakingRatio > 0.75) {
          setPaceFeedback("Speaking very fast ‚Äî slow down slightly");
        } else if (speakingRatio < 0.4) {
          setPaceFeedback("Too many pauses ‚Äî try to be smoother");
        } else {
          setPaceFeedback("Good speaking pace üëç");
        }
        lastPaceCheckRef.current = now;
      }

      rafRef.current = requestAnimationFrame(analyze);
    };

    analyze();

    return () => {
      if (rafRef.current) cancelAnimationFrame(rafRef.current);
      audioContext.close();
    };
  }, [stream]);

  // üîπ Post-interview summary (ONCE)
  useEffect(() => {
    if (!recording && confidenceScore > 0 && !summary) {
      const ratio =
        totalFramesRef.current > 0
          ? speakingFramesRef.current / totalFramesRef.current
          : 0;

      setSummary(
        `You demonstrated a ${confidenceLabel.toLowerCase()} level of confidence with ${
          ratio > 0.6 ? "consistent engagement" : "frequent pauses"
        }. Focus on maintaining steady volume, controlled pacing, and direct eye contact for even stronger delivery.`
      );
    }
  }, [recording, confidenceScore, confidenceLabel, summary]);

  return (
    <div style={{ marginTop: 16, padding: 14, background: "#f8f9ff", borderRadius: 12 }}>
      <h4>üé§ Live Interview Feedback</h4>

      <p>Status: <strong>{speaking ? "Speaking" : "Silent"}</strong></p>

      <div style={{ height: 10, background: "#ddd", borderRadius: 6 }}>
        <div
          style={{
            height: "100%",
            width: `${Math.min(volume * 400, 100)}%`,
            background: "#3a7fff",
          }}
        />
      </div>

      <p><strong>{confidenceText}</strong></p>

      <p>üéØ Confidence Score (45s avg): <strong>{confidenceScore}/100</strong></p>
      <p>üè∑ Rating: <strong>{confidenceLabel}</strong></p>

      <p>üó£ Pace: <strong>{paceFeedback}</strong></p>
      <p>üßç Presence: <strong>{postureFeedback}</strong></p>

      {summary && (
        <>
          <hr />
          <p><strong>üß† AI Summary</strong></p>
          <p>{summary}</p>
        </>
      )}
    </div>
  );
}
