import React, { useEffect, useState } from "react";

export default function PostureAnalyzer({ videoRef }) {
  const [eyeContact, setEyeContact] = useState("Analyzing...");
  const [posture, setPosture] = useState("Analyzing...");

  useEffect(() => {
    if (!videoRef?.current) return;

    const interval = setInterval(() => {
      // ‚ö†Ô∏è Placeholder logic (no ML yet)
      // Later we‚Äôll replace this with MediaPipe / TensorFlow.js

      const eyeOk = Math.random() > 0.3;
      const postureOk = Math.random() > 0.4;

      setEyeContact(
        eyeOk ? "Good eye contact üëÄ" : "Look at the camera more"
      );

      setPosture(
        postureOk ? "Upright posture üëç" : "Sit straight ‚ö†Ô∏è"
      );
    }, 3000); // update every 3s (not too fast)

    return () => clearInterval(interval);
  }, [videoRef]);

  return (
    <div
      style={{
        marginTop: 16,
        padding: 14,
        background: "#fff7ed",
        borderRadius: 12,
        border: "1px solid #fed7aa",
      }}
    >
      <h4>üßç Posture & Presence</h4>
      <p>üëÄ Eye Contact: <strong>{eyeContact}</strong></p>
      <p>ü™ë Posture: <strong>{posture}</strong></p>
    </div>
  );
}
