import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import GlitchTransition from "../components/GlitchTransition";
import "../styles/home.css";

export default function Home() {
  const navigate = useNavigate();
  const [isGlitching, setIsGlitching] = useState(false);

  const triggerGlitch = (route) => {
    setIsGlitching(true);

    setTimeout(() => {
      navigate(route);
      setIsGlitching(false);
    }, 1200); // glitch duration
  };

  return (
    <div className="home-root">

      {/* ⚡ Electric Glitch Transition */}
      {isGlitching && <GlitchTransition />}

      <div className="home-inner container">

        {/* ================= HEADER ================= */}
        <header className="topbar">
          <div className="brand">
            <img src="/logo192.png" alt="Marg AI" className="brand-logo" />
            <div className="brand-text">
              <div className="brand-name neon-text">Marg AI</div>
              <div className="brand-sub">AI Interview Intelligence System</div>
            </div>
          </div>
        </header>

        {/* ================= HERO ================= */}
        <section className="hero">

          {/* LEFT SIDE */}
          <div className="hero-left">

            <h1 className="hero-title">
              Practice. Analyze. Dominate.
            </h1>

            <p className="hero-sub">
              Real-time AI evaluation of your communication, posture,
              confidence, pace and engagement — all in one futuristic dashboard.
            </p>

            <div className="hero-ctas">
              <button
                className="btn sci-fi"
                onClick={() => triggerGlitch("/mock-interview")}
              >
                ▶ Start Mock Interview
              </button>

              <button
                className="btn sci-fi-outline"
                onClick={() => triggerGlitch("/practice")}
              >
                ⌨ Practice Coding
              </button>
            </div>

            <div className="trust">
              <div className="trust-pill">⚡ Live Confidence Detection</div>
              <div className="trust-pill">⚡ AI Voice & Engagement Scoring</div>
              <div className="trust-pill">⚡ Real Performance Analytics</div>
            </div>

          </div>

          {/* RIGHT SIDE (TV SCREEN) */}
          <div className="hero-right">

            <div className="tv-frame">
              <video
                className="demo-video"
                autoPlay
                loop
                muted
                playsInline
              >
                <source src="/demo-video.mp4" type="video/mp4" />
              </video>

              {/* TV glow overlay */}
              <div className="tv-overlay"></div>

              {/* scanline effect */}
              <div className="tv-scanline"></div>
            </div>

          </div>

        </section>

        {/* ================= FOOTER ================= */}
        <footer className="home-footer">
          © {new Date().getFullYear()} Marg AI • Futuristic Interface Mode
        </footer>

      </div>
    </div>
  );
}
